import {palette} from './palette';


export const components ={

  MuiTextField: {
    styleOverrides: {
      root: {

        '--TextField-brandBorderColor': '#c7c7c7',
        '--TextField-brandBorderHoverColor': '#c7c7c7',
        '--TextField-brandBorderFocusedColor': '#FFF',
        '& label.Mui-focused': {
          color: '#c7c7c7',
        },
      },
    },
  },
  MuiOutlinedInput: {
    styleOverrides: {
      root: {
        backgroundColor: "#FFF",
        '&.Mui-focused': {
          borderColor: '#F3F', // Border color when focused
        },
        },
      },
    },
  }
