import {palette} from './palette';


export const components ={

  MuiTextField: {
    styleOverrides: {
      root: {

        '--TextField-brandBorderColor': '#D2D2D2FF',
        '--TextField-brandBorderHoverColor': '#D2D2D2FF',
        '--TextField-brandBorderFocusedColor': '#D2D2D2FF',
        '& label.Mui-focused': {
          color: '#D2D2D2FF',
        },
      },
    },
  },
  MuiOutlinedInput: {
    styleOverrides: {
      root: {
        backgroundColor: "#FFF",
        '&.Mui-focused': {
          borderColor: 'green', // Border color when focused
        },
        },
      },
    },
  }
